import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status
def filepath ="C:\\SDET -Full Stack Tester\\API Testing\\ProjectSubmitted_RestAPI\\TestDataSheets\\CustomerTestData.xls"
File Excel = new File (filepath)
//I wnat to fetch the Workbook and the sheet which contain the data
Workbook wb= Workbook.getWorkbook(Excel)
//I want to fecth sheet
Sheet sh=wb.getSheet("Sheet1")
//I want to fetch the number of rows and and no of column
def rowsize =sh.getRows().toInteger()
def columnsize=sh.getColumns().toInteger()

log.info "$rowsize and $columnsize"

//fetch the value in the variable
for (def i=1;i<rowsize;i++)
{
	for(def j=0;j<columnsize;j++)
	{
		if(j==0)
		{
			firstname=sh.getCell(j,i).getContents()
			log.info "$firstname"	
		}
		else if (j==1)
		{
			lastname=sh.getCell(j,i).getContents()
			log.info "$lastname"	
		}
		else if (j==2)
		{
			email=sh.getCell(j,i).getContents()
			log.info "$email"	
		}
		else if (j==3)
		{
			password=sh.getCell(j,i).getContents()
			log.info "$password"	
		}
		else if (j==4)
		{
			confirm_password=sh.getCell(j,i).getContents()
			log.info "$confirm_password"	
		}
		else if (j==5)
		{
			phone=sh.getCell(j,i).getContents()
			log.info "$phone"	
		}
	}

	// get TestCase
	//def tc = testRunner.testCase.testSuite.project.testSuites['Automation - Rest API Project Submission'].testCases['TC030_AddCustomer and Products']
	def tc = testRunner.testCase.testSuite.testCases['TC030_Add_Update_Delete_Customer']

	// set credential properties from the script itself
	tc.testSteps["Properties"].setPropertyValue("FirstName", firstname)
	tc.testSteps["Properties"].setPropertyValue("LastName", lastname)
	tc.testSteps["Properties"].setPropertyValue("Email", email)
	tc.testSteps["Properties"].setPropertyValue("Password", password)
	tc.testSteps["Properties"].setPropertyValue("Confirm_Password", confirm_password)
	tc.testSteps["Properties"].setPropertyValue("Telephone", phone)

	// run test synchrouously
	def runner = tc.run( null, false )

	// show the test case result status
	log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
	log.info "Number of test steps which ran: " + runner.results.size()

	// assert that it didn't fail
	assert runner.status != Status.FAILED : runner.reason

	// assert that only four steps ran!!
	//assert runner.results.size() == 4

}

import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status
def filepath ="C:\\SDET -Full Stack Tester\\API Testing\\ProjectSubmitted_RestAPI\\TestDataSheets\\ProductTestData.xls"
File Excel = new File (filepath)
//I wnat to fetch the Workbook and the sheet which contain the data
Workbook wb= Workbook.getWorkbook(Excel)
//I want to fecth sheet
Sheet sh=wb.getSheet("Sheet1")
//I want to fetch the number of rows and and no of column
def rowsize =sh.getRows().toInteger()
def columnsize=sh.getColumns().toInteger()

log.info "$rowsize and $columnsize"

//fetch the value in the variable
for (def i=1;i<rowsize;i++)
{
	for(def j=0;j<columnsize;j++)
	{
		if(j==0)
		{
			model=sh.getCell(j,i).getContents()
			log.info "$model"	
		}
		else if (j==1)
		{
			quantity=sh.getCell(j,i).getContents()
			log.info "$quantity"	
		}
		else if (j==2)
		{
			price=sh.getCell(j,i).getContents()
			log.info "$price"	
		}		
		else if (j==3)
		{
			name=sh.getCell(j,i).getContents()
			log.info "$name"	
		}
		else if (j==4)
		{
			meta_title=sh.getCell(j,i).getContents()
			log.info "$meta_title"	
		}
		else if (j==5)
		{
			product_category=sh.getCell(j,i).getContents()
			log.info "$product_category"	
		}
	}

	// get TestCase
	//def tc = testRunner.testCase.testSuite.project.testSuites['Automation - Rest API Project Submission'].testCases['TC030_AddCustomer and Products']
	def tc = testRunner.testCase.testSuite.testCases['TC030_Add_Update_Products']

	// set credential properties from the script itself
	tc.testSteps["Properties"].setPropertyValue("Model", model)
	tc.testSteps["Properties"].setPropertyValue("Quantity", quantity)
	tc.testSteps["Properties"].setPropertyValue("Price", price)
	tc.testSteps["Properties"].setPropertyValue("Name", name)
	tc.testSteps["Properties"].setPropertyValue("Meta_Title", meta_title)
	 tc.testSteps["Properties"].setPropertyValue("Product_Category", product_category)
	// run test synchrouously
	def runner = tc.run( null, false )

	// show the test case result status
	log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
	log.info "Number of test steps which ran: " + runner.results.size()

	// assert that it didn't fail
	assert runner.status != Status.FAILED : runner.reason

	// assert that only four steps ran!!
	//assert runner.results.size() == 4

}

