	Password=context.expand('${JDBC_AddCustomer#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMER1.PASSWORD}')
	Confirm=context.expand('${JDBC_AddCustomer#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMER1.CONFIRM}')
	Phone=context.expand('${JDBC_AddCustomer#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMER1.TELEPHONE}')

	log.info " Firstname: $FirstName"
	log.info " Lastname: $LastName"
	log.info " Email: $Email"
	log.info " Password: $Password"
	log.info " Confirm Password: $Confirm"
